const state = {
    ayantdroits : [],
    checked :  [], 
    numberItems : 50, 
    loader : 10
    
}; 

const getters  ={
    allAyantdroits : (state) => state.ayantdroits,
    allChecked : (state) => state.checked,
    numberItems : (state) => state.numberItems,
    allLoader : (state) => state.loader

};

const actions = {
    async loadAyantdroits({commit}){
        commit('fetchLoader')
        const response = await fetch(`http://localhost:3000/AyantDroits?_limit=${state.numberItems}`);
        await new Promise(resolve => setTimeout(resolve, 4000));
        const res = await response.json()
        commit('loadAyantdroits', res)
        commit('fetchChecked')
        commit('fetchNumberItems')
        commit('fetchLoader')
      
       
    }

};

const mutations = {
    loadAyantdroits : (state, ayantdroits) => state.ayantdroits = ayantdroits, 
    fetchChecked : (state) => state.checked = Array(state.ayantdroits.length).fill(false),
    fetchNumberItems : (state) => state.numberItems  += 50,
    fetchLoader : function(state) {
        if(state.loader == 0 && state.numberItems == 50){    
            state.loader = 10
        }else if(state.loader == true){
            state.loader = 3
        }else{
            state.loader = 10
        }
    }

};


export default {
    state, 
    getters, 
    actions, 
    mutations, 

}
