const state = {
    ayantdroits : [],
    checked :  Array(getChecked()).fill(false)
}; 

const getters  ={
    allAyantdroits : (state) => state.ayantdroits,
    checked : (state) => state.checked

};

const actions = {
    async fetchAyantdroits({commit}){
        const response = await fetch("http://localhost:3000/AyantDroits");
        const res = await response.json();
        commit('fetchAyantdroits', res)
       
    }

};

const mutations = {
    fetchAyantdroits : (state, ayantdroits) => state.ayantdroits = ayantdroits

};


export default {
    state, 
    getters, 
    actions, 
    mutations, 

}
