const state = {
    fields : [
        {identifiant : '', isActive : false },
        {soldeMin : '', isActive : false },
        {soldeMax: '', isActive : false },
        {contratMin: '', isActive : false },
        {contratMax: '', isActive : false},
        {annee : '', isActive : false},
    ]   
}

const getters = {
    allFields : (state) => state.fields
}

const actions = {
    handleFocus({commit}, number){
        commit('changeIsActive', number, true)
    }, 
    handleBlur({commit}, number){
        if(Object.values(this.fields[number])[0] == ''){
            commit('changeIsActive', number, false)
        }         
    }, 
    removeFilter({commit}){
        commit('removeFilter')
    }

}

const mutations = {
    changeIsActive : (state, number, bool) => state.fields[number].isActive= bool, 
    removeFilter : (state) => state.fields.forEach(objects => {objects[Object.keys(objects)[0]] = ''; objects.isActive = false})
}


export default {
    state, 
    getters, 
    actions, 
    mutations
}
