const state = {
    ayantdroits : [],
    checked :  [], 
    numberItems : 50, 
    loader : 10,
    currentColSorted : "",
    currentSortType :""
    
}; 

const getters  ={
    allAyantdroits : (state) => state.ayantdroits,
    allChecked : (state) => state.checked,
    numberItems : (state) => state.numberItems,
    allLoader : (state) => state.loader,
    currentColSorted : (state) => state.currentColSorted

};

const actions = {
    async loadAyantdroits({commit}){
        commit('fetchLoader', true)
        const response = await fetch(`http://localhost:3000/AyantDroits?_limit=${state.numberItems}`);
        await new Promise(resolve => setTimeout(resolve, 4000));
        const res = await response.json()
        commit('loadAyantdroits', res)
        commit('sortLoad')
        commit('fetchChecked')
        commit('fetchNumberItems')
        commit('fetchLoader', false)
    }, 

    sortColumn({commit}, field){
      commit('sortObjectArray', field)
    }

};

const mutations = {
    loadAyantdroits : (state, ayantdroits) => state.ayantdroits = ayantdroits, 
    fetchChecked : (state) => state.checked = Array(state.ayantdroits.length).fill(false),
    fetchNumberItems : (state) => state.numberItems  += 50,
    fetchLoader : function(state, boolLoading) {
        if(boolLoading &&state.numberItems == 50){
            state.loader = 10
        }else if(boolLoading){
            state.loader = 3
        }else{
            state.loader = 0
        }
    }, 
    sortLoad : function(state, field){
        let n= 0
        n = state.currentSortType == 'ASC' ? n= 1 : n = -1
        if(state.currentSortType != ""){
            console.log("*****")
            state.ayantdroits.sort((a, b) => {   
                if(a[field] > b[field]){
                    return 1
                }else if(a[field] < b[field]){
                    return -1
                }else{
                    return 0
                }
            })
        }
    },
    sortObjectArray : function(state, field) {
        if(state.currentColSorted == field){
            if(state.ayantdroits[0][field] < state.ayantdroits[1][field]){
                state.ayantdroits.sort((a, b) => {
            
                    if(a[field] > b[field]){
                        return -1
                    }else if(a[field] < b[field]){
                        return 1
                    }else{
                        return 0
                    }
                })
                state.currentSortType = 'ASC'
            }else{
                state.ayantdroits.sort((a, b) => {
            
                    if(a[field] < b[field]){
                        return -1
                    }else if(a[field] > b[field]){
                        return 1
                    }else{
                        return 0
                    }
                })
                state.currentSortType = 'DSC'
            }
        }else{
            state.ayantdroits.sort((a, b) => {
            
                if(a[field] < b[field]){
                    return -1
                }else if(a[field] > b[field]){
                    return 1
                }else{
                    return 0
                }
            })

            state.currentColSorted = field
            state.currentSortType =  'ASC'
        }
       
    }
}





export default {
    state, 
    getters, 
    actions, 
    mutations, 

}
